import React, { Component } from "react";
import { MDBBtn, MDBContainer, MDBCard } from "mdbreact";

export default class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
      taskList: []
    };
  }

  changeUserInput(input) {
    this.setState({
      userInput: input
    });
  }

  addTask(input) {
    if (input !== "") {
      let list = [...this.state.taskList];
      list.push({ input: input, key: list.length });
      this.setState({
        userInput: "",
        taskList: list
      });
    }
  }

  removeTask() {
    let list = [...this.state.taskList];
    list.pop();
    this.setState({
      taskList: list
    });
  }

  clearList() {
    this.setState({
      taskList: []
    });
  }

  render() {
    return (
        <MDBContainer>
  <MDBCard style={{ width: "50vw", marginTop: "1rem", padding: "30px" }}>
      <div className="form-group">
        <label htmlFor="userInput">what are you going to do today?</label>
        <input
        id ="userInput"
        className="form-control"
        type="text"
          onChange={e => this.changeUserInput(e.target.value)}
          value={this.state.userInput}
        />
        <MDBBtn color="blue-grey" onClick={() => this.addTask(this.state.userInput)}>add</MDBBtn>
        <ul>
          {this.state.taskList.map(task => (
            <li key={task.key}>
              task no. {task.key + 1}: {task.input}
            </li>
          ))}
        </ul>
        <MDBBtn outline onClick={() => this.removeTask(this.state.userInput)}>
          remove
        </MDBBtn>
        <MDBBtn onClick={() => this.clearList(this.state.userInput)}>
          clear
        </MDBBtn>

      </div>
      </MDBCard>
</MDBContainer>
    );
  }
}
