import React, { Component } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBListGroup,
  MDBListGroupItem,
  MDBFormInline
} from "mdbreact";

export default class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
      taskList: []
    };
  }

  changeUserInput(input) {
    this.setState({
      userInput: input
    });
  }

  addTask(input) {
    if (input !== "") {
      let list = [...this.state.taskList];
      list.push({ input: input, key: list.length });
      this.setState({
        userInput: "",
        taskList: list
      });
    }
  }

  removeTask() {
    let list = [...this.state.taskList];
    list.pop();
    this.setState({
      taskList: list
    });
  }

  clearList() {
    this.setState({
      taskList: []
    });
  }

  render() {
    return (
      <div>
        <MDBCard style={{ width: "80vw", marginTop: "10vh", padding: "30px" }}>

          <div class="form-row" style={{backgroundColor: "pink"}}>

            <input
              id="userInput"
              className="col-8"
              type="text"
              onChange={e => this.changeUserInput(e.target.value)}
              value={this.state.userInput}
              placeholder="what are you going to do today?"
            />

            <MDBBtn
            className="col-3"
            style={{backgroundColor: "red", padding: "0px"}}
              color="blue-grey"
              onClick={() => this.addTask(this.state.userInput)}
            >
              add
            </MDBBtn>
          </div>

          {this.state.taskList.length == 0 ? (
            <div>looks like you've got nothing to do! </div>
          ) : null}

          <MDBListGroup>
            {this.state.taskList.map(task => (
              <MDBListGroupItem key={task.key}>
                <em style={{ fontWeight: "500", color: "#2bbbad" }}>
                  task no. {task.key + 1}:
                </em>{" "}
                {task.input}
              </MDBListGroupItem>
            ))}
          </MDBListGroup>
        </MDBCard>

        <div
          style={{
            width: "80vw",
            display: "flex",
            justifyContent: "flex-end",
            padding: "10px"
          }}
        >
          <MDBBtn outline onClick={() => this.removeTask(this.state.userInput)}>
            remove
          </MDBBtn>
          <MDBBtn onClick={() => this.clearList(this.state.userInput)}>
            clear
          </MDBBtn>
        </div>



<form>

  <div class="form-row align-items-center">

    <div class="col-10">

      <label class="sr-only" for="inlineFormInput">Name</label>
      <input type="text" class="form-control mb-2" id="inlineFormInput" placeholder="Jane Doe"></input>
    </div>


    <div class="col-2">
      <button type="submit" class="btn btn-primary btn-md mt-0">Submit</button>
    </div>
  </div>

</form>

      </div>
    );
  }
}
