import React, { Component } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBListGroup,
  MDBListGroupItem,
  MDBFormInline
} from "mdbreact";
import styles from "../css-modules/TodoList.module.css";

export default class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
      taskList: []
    };
  }

  changeUserInput(input) {
    this.setState({
      userInput: input
    });
  }

  addTask(input) {
    if (input !== "") {
      let list = [...this.state.taskList];
      list.push({ input: input, key: list.length });
      this.setState({
        userInput: "",
        taskList: list
      });
    }
  }

  removeTask() {
    let list = [...this.state.taskList];
    list.pop();
    this.setState({
      taskList: list
    });
  }

  clearList() {
    this.setState({
      taskList: []
    });
  }

  render() {
    return (
      <div>
        <MDBCard className={styles.card}>
          <div class={styles.user-input}>
            <label class="sr-only" for="userInput">
              Task
            </label>
            <input
              id="userInput"
              // className="col-md-10 col-sm-12"
              type="text"
              onChange={e => this.changeUserInput(e.target.value)}
              value={this.state.userInput}
              placeholder="what are you going to do today?"
              style={{ height: "8vh" }}
            />
            <MDBBtn
              className={styles.btn}
              color="blue-grey"
              onClick={() => this.addTask(this.state.userInput)}
            >
              add
            </MDBBtn>
          </div>


          {this.state.taskList.length == 0 ? (
            <div>looks like you've got nothing to do! </div>
          ) : null}

          <MDBListGroup>
            {this.state.taskList.map(task => (
              <MDBListGroupItem key={task.key}>
                <em style={{ fontWeight: "500", color: "#2bbbad" }}>
                  task no. {task.key + 1}:
                </em>{" "}
                {task.input}
              </MDBListGroupItem>
            ))}
          </MDBListGroup>
        </MDBCard>

        <div
          style={{
            width: "80vw",
            display: "flex",
            justifyContent: "flex-end",
            padding: "10px"
          }}
        >
          <MDBBtn outline 
          onClick={() => this.removeTask(this.state.userInput)}
          className={styles.btn}>
            remove
          </MDBBtn>
          <MDBBtn 
          onClick={() => this.clearList(this.state.userInput)}
          className={styles.btn}>
            clear
          </MDBBtn>
        </div>


      </div>
    );
  }
}
