import React, { Component } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBCard,
  MDBListGroup,
  MDBListGroupItem,
  MDBFormInline
} from "mdbreact";

export default class TodoList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInput: "",
      taskList: []
    };
  }

  changeUserInput(input) {
    this.setState({
      userInput: input
    });
  }

  addTask(input) {
    if (input !== "") {
      let list = [...this.state.taskList];
      list.push({ input: input, key: list.length });
      this.setState({
        userInput: "",
        taskList: list
      });
    }
  }

  removeTask() {
    let list = [...this.state.taskList];
    list.pop();
    this.setState({
      taskList: list
    });
  }

  clearList() {
    this.setState({
      taskList: []
    });
  }

  render() {
    return (
      <MDBContainer>
        <MDBCard style={{ width: "80vw", marginTop: "10vh", padding: "30px" }}>
          <MDBFormInline>
            <input
              id="userInput"
              className="form-control"
              type="text"
              onChange={e => this.changeUserInput(e.target.value)}
              value={this.state.userInput}
            />

            <MDBBtn
              color="blue-grey"
              onClick={() => this.addTask(this.state.userInput)}
            >
              add
            </MDBBtn>
          </MDBFormInline>


          <MDBListGroup>
            {this.state.taskList.map(task => (
              <MDBListGroupItem key={task.key}>
                task no. {task.key + 1}: {task.input}
              </MDBListGroupItem>
            ))}
          </MDBListGroup>
          </MDBCard>

          <div style={{ width: "100%", display: "flex", justifyContent: "space-around" }}>
            <MDBBtn
              outline
              onClick={() => this.removeTask(this.state.userInput)}
            >
              remove
            </MDBBtn>
            <MDBBtn onClick={() => this.clearList(this.state.userInput)}>
              clear
            </MDBBtn>
          </div>

      </MDBContainer>
    );
  }
}
